# verifies the phone number

import requests
import core.Solver4d
import core.Log4p
import time

class Verify:

    def __init__(self, ptoken, stoken, discord_token, discord_password):

        self.ptoken = ptoken

        self.stoken = stoken

        self.discord_token = discord_token

        self.Solver = core.Solver4d.Solver(stoken)

        self.Logger = core.Log4p.Logger()

        self.discord_password = discord_password

        self.country_number = 372


    def verify_phone_number(self):

        #-------------------------------------------------------------------------#
        #                        REGISTERING PHONE NUMBER                         #
        #-------------------------------------------------------------------------#
        
        try:
            result = requests.get(f"https://onlinesim.ru/api/getNum.php?apikey={self.ptoken}&service=discord&number=true&country={self.country_number}").json()
            if result.get("response")=="NO_NUMBER": 
                self.Logger.error(__name__, "No number was generated by onlinesim, please coose another country_number!")
        except Exception as e:
            self.Logger.error(__name__, "problem while registering a phone number task - " + str(e))

        # phone number
        number = result.get("number")

        # TODO BAN DETECTOR post without catpcha

        self.Logger.content(__name__, "Number: ", number)

        tzid = result.get("tzid")
        self.Logger.content(__name__, f"Onlinesim tzid: ", tzid)


        self.Logger.info(__name__, "succesfully registered phone number verfication task")

        #-------------------------------------------------------------------------#
        #                       SUBMITTING NUMBER TO DISCORD                      #
        #-------------------------------------------------------------------------#

        json = {
            "captcha_key": self.Solver.get_captcha_key(isPhone=True),
            "phone" : number,
            "change_phone_reason":"user_action_required"
        }

        headers = {
          "accept": "*/*",
          "accept-encoding": "gzip, deflate, br",
          "accept-language": "it",
          "authorization": self.discord_token,
          "content-type": "application/json",
          "origin": "https://discord.com",
          "referer": "https://discord.com/channels/@me",
          "sec-ch-ua":'Google Chrome";v="93", " Not;A Brand";v="99", "Chromium";v="93',
          "sec-ch-ua-mobile": "?0",
          "sec-ch-ua-platform": "Windows",
          "sec-fetch-dest": "empty",
          "sec-fetch-mode": "cors",
          "sec-fetch-site": "same-origin",
          "user-agent": "Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/93.0.4577.82 Safari/537.36",
          "x-debug-options": "bugReporterEnabled",
          "x-super-properties": "",
        }
        try:
            with requests.post("https://discord.com/api/v9/users/@me/phone", json=json, headers=headers) as response: pass #expects no response
        except Exception as e: 
            self.Logger.error(__name__,"problem while initing phone number verficiation request - "+str(e))

        self.Logger.info(__name__, "requested phone number verify")

        #-------------------------------------------------------------------------#

        uncaught_sms_times = 0
        while True:

            # If `uncaught_sms_times` is 15 then pass and generate new number.
            if uncaught_sms_times >= 15:
                self.Logger.warn(__name__,"PHONE NUMBER BANNED")
                self.verify_phone_number()
                break

            # Getting sms message ( discord code that was sent to the phone number ).
            with requests.get(f"https://onlinesim.ru/api/getState.php?apikey={self.ptoken}&tzid={tzid}") as response:
                if response.json()[0].get("msg") != None:
                    code = response.json()[0].get("msg")
                    break

                # If there is no message increment `uncaught_sms_times`
                else:
                    time.sleep(3)
                    self.Logger.warn(__name__, f"Didn't get the sms yet, checking again.")
                    
                    uncaught_sms_times = uncaught_sms_times+1

        #-------------------------------------------------------------------------#

        self.Logger.info(__name__, "got sms")
        json = {
            "code" : code,
            "phone" : number,
        }

        headers = {
          "accept": "*/*",
          "accept-language": "it",
          "authorization": self.discord_token,
          "content-type": "application/json",
          "origin": "https://discord.com",
          "referer": "https://discord.com/channels/@me",
          "sec-ch-ua":
            'Google Chrome";v="93", " Not;A Brand";v="99", "Chromium";v="93',
          "sec-ch-ua-mobile": "?0",
          "sec-ch-ua-platform": "Windows",
          "sec-fetch-dest": "empty",
          "sec-fetch-mode": "cors",
          "sec-fetch-site": "same-origin",
          "user-agent":
            "Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/93.0.4577.82 Safari/537.36",
          "x-debug-options": "bugReporterEnabled",
          "x-super-properties":
            "",
        }

        with requests.post("https://discord.com/api/v9/phone-verifications/verify",json=json,headers=headers) as response:
            final_token = response.json()['token']


        #-------------------------------------------------------------------------#
        #                       SUBMITTING DISCORD PASSWORD                       #
        #-------------------------------------------------------------------------#

        json = {
            "phone_token" : final_token,
            "password" : self.discord_password,
            "change_phone_reason" : "user_action_required",
        }

        self.Logger.info(__name__, "requested change")

        headers = {
          "accept": "*/*",
          "accept-language": "it",
          "authorization": self.discord_token,
          "content-type": "application/json",
          "origin": "https://discord.com",
          "referer": "https://discord.com/channels/@me",
          "sec-ch-ua":
            'Google Chrome";v="93", " Not;A Brand";v="99", "Chromium";v="93',
          "sec-ch-ua-mobile": "?0",
          "sec-ch-ua-platform": "Windows",
          "sec-fetch-dest": "empty",
          "sec-fetch-mode": "cors",
          "sec-fetch-site": "same-origin",
          "user-agent":
            "Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/93.0.4577.82 Safari/537.36",
          "x-debug-options": "bugReporterEnabled",
          "x-super-properties":
            "",
        }

        with requests.post("https://discord.com/api/v9/users/@me/phone",json=json,headers=headers) as response: pass

        self.Logger.info(__name__, "complete")


